package ch01;

import java.util.*;

public class JoiningStrings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1,s2,s3;
		Scanner sc = new Scanner(System.in);
		s1=sc.nextLine();
		s2=sc.nextLine();
		s3=sc.nextLine();
		sc.close();
		String res = s1+" "+s2+" "+s3;
		System.out.println(res);
			}

}
